<style lang="scss">
.header {
  padding: 44px 20px 0;
  position: fixed;
  z-index: 1;
  top: 0;
  width: 100%;
  background: transparent;
  transition: 0.3s;
  padding-bottom: 12px;
  @media (max-width: $DesktopWidth - 1px) {
    padding-right: 60px;
    padding-left: 60px;
  }
  @media (max-width: $TableWidth - 1px) {
    padding-right: 16px;
    padding-left: 16px;
  }
  &::before {
    content: "";
    height: 0px;
    width: 100%;
    left: 0;
    bottom: 0;
    position: absolute;
    z-index: -1;
    transition: ease-in-out all 0.2s;
  }
}
.header-content__wrap {
  justify-content: space-between;
  display: flex;
  max-width: 1080px;
  width: 100%;
  margin: 0 auto;
  @media (max-width: $MidlleWidth - 1px) {
    padding-top: 8px;
    max-width: 100%;
  }
}
.social-list {
  display: flex;
  &__item-wrap {
    opacity: 0.6;
    margin-right: 22px;
    &:hover {
      opacity: 1;
    }
    &:last-child {
      margin: 0;
    }
  }
}

.logo {
  fill: black;
  @media (max-width: $TableWidth - 1px) {
    width: 110px;
    height: 25px;
  }
}
.social-list__item {
  display: flex;
  background: white;
  border-radius: 50%;
  width: 40px;
  height: 40px;
  justify-content: center;
  align-items: center;
  @media (max-width: $TableWidth - 1px) {
    width: 25px;
    height: 25px;
  }
  .social-icon {
    fill: #ff9e27;
  }
  &:hover {
    opacity: 1;
    .social-icon {
      fill: #ed5e42;
    }
  }
}
</style>